"use strict";var sections=document.querySelector(".sections").querySelectorAll("section"),nav=document.querySelector("#sticky-nav_nav"),navHeight=nav.offsetHeight,navTop=nav.offsetTop,stickykNav=function(){window.pageYOffset>=navTop?nav.classList.add("position-fixed"):nav.classList.remove("position-fixed")};window.addEventListener("scroll",function(e){var t=this.pageYOffset,s=sections.length;stickykNav();for(var o=0;o<s;o++){var i=sections[o].offsetTop-navHeight,n=i+sections[o].offsetHeight;if(i<=t&&t<=n){console.log(sections[o]);for(var a=nav.querySelectorAll("a"),c=0;c<a.length;c++)a[c].classList.remove("active");document.querySelector("a."+sections[o].id).classList.add("active")}}});