"use strict";var sections=document.querySelector(".sections").querySelectorAll("section"),nav=document.querySelectorAll("nav")[1],navHeight=nav.offsetHeight;window.addEventListener("scroll",function(e){for(var t=this.pageYOffset,s=sections.length,o=(e.target,0);o<s;o++){var n=sections[o].offsetTop-navHeight,c=n+sections[o].offsetHeight;if(n<=t&&t<=c){console.log(sections[o]);for(var i=nav.querySelectorAll("a"),r=0;r<i.length;r++)i[r].classList.remove("active");document.querySelector("a."+sections[o].id).classList.add("active")}}});